import React from 'react';
import { useForm } from 'react-hook-form';
import styled from 'styled-components'

const NewArticleInputs = styled.div`
display: flex;
  flex-direction: column;
  align-items: center;
`

export default function NewArticle() {
  const { register, handleSubmit, errors } = useForm();
  const onSubmit = data => console.log(data);
  console.log(errors);

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
        <NewArticleInputs>
      <input type="text" placeholder="Article Name" name="Article Name" ref={register({required: true, max: 13, min: 3, maxLength: 25})} />
      <input type="url" placeholder="Article Link" name="Article Link" ref={register} />
      <textarea placeholder='Description of Article'name="Article Description" ref={register} />
      <input type="submit" />
      </NewArticleInputs>
    </form>
  );
}
